sysauto_script.script:
// Get today's time and date
	var rightNow = new GlideDateTime();

	// Query the database for NeedIt Task records with Due date field values older
	// than the current time.  Only return NeedIt Task records that do not have
	// a State field value of Closed Complete.
	var dueNITasks = new GlideRecord('x_58872_needit_needit_task');
	dueNITasks.addQuery('due_date','>=',rightNow);
	dueNITasks.addQuery('due_date','<',gs.daysAgo(-4));
	dueNITasks.addQuery('state','!=',3);

	dueNITasks.query();
	// Write a log message for each overdue NeedIt Task Record
   //Generate an event for each due soon Needit Task Record
	while(dueNITasks.next()){
		gs.info("Overdue NeedIt Task record = " + dueNITasks.number);
		gs.eventQueue('x_58872_needit.dueSoonNeeditTasks',dueNITasks,dueNITasks.number,gs.getUserName());
	}
